options.customCss:
/* --- FUENTE Y COLORES CORPORATIVOS GINEZ --- */
@import url("https://fonts.googleapis.com/css2?family=Avenir+Next+LT+Pro+Light:wght@300&display=swap");

:root {
  --color-azul: #0E0C9B;
  --color-rojo: #C41F1A;
  --color-blanco: #f8f9fb;
  --font-body: 'Avenir Next LT Pro Light', Arial, sans-serif;
}

/* --- FONDO GENERAL --- */
body {
  background: linear-gradient(to bottom right, #e3edf7, #f9fbfc);
  font-family: var(--font-body);
  margin: 0;
  padding: 0;
}

/* --- CONTENEDOR PRINCIPAL --- */
.form-wrapper {
  background-color: var(--color-blanco);
  border-radius: 30px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.25);
  padding: 40px 60px;
  max-width: 1080px;
  margin: 100px auto;
  position: relative;
  text-align: center;
}

/* --- LOGO SUPERIOR --- */
.form-logo {
  display: block;
  width: 45%;
  max-width: 380px;
  margin: 0 auto 25px auto;
}

/* --- T√çTULO Y DESCRIPCI√ìN --- */
h1, h2, h3, .form-title {
  color: var(--color-azul);
  text-align: center;
  font-size: 1.6rem;
  font-weight: 750;
  margin-bottom: 23px;
}

p, .form-description {
  text-align: justify;
  font-size: 15px;
  color: #333;
  margin-bottom: 30px;
}

/* --- CAMPOS DEL FORMULARIO --- */
input, select, textarea {
  border: 1px solid #cbd5e1;
  border-radius: 15px;
  padding: 12px 16px;
  width: 90%;
  font-size: 16px;
  font-family: var(--font-body);
  box-shadow: 0 1px 4px rgba(0, 0, 0, 0.15);
  transition: all 0.2s ease;
}

input:focus, select:focus, textarea:focus {
  border-color: var(--color-azul);
  outline: none;
  box-shadow: 0 0 6px rgba(13, 59, 102, 0.3);
}

/* --- BOT√ìN SUBMIT --- */
button[type="submit"], .n8n-form-submit-button {
  background: linear-gradient(to right, var(--color-azul), var(--color-rojo));
  color: white;
  border: none;
  border-radius: 15px;
  padding: 12px;
  width: 90%;
  font-size: 18px;
  font-weight: 900;
  letter-spacing: 0.75px;
  cursor: pointer;
  transition: transform 0.2s ease, opacity 0.2s ease;
}

button[type="submit"]:hover, .n8n-form-submit-button:hover {
  transform: scale(1.02);
  opacity: 0.9;
}

/* --- RESPONSIVE --- */
@media (max-width: 600px) {
  .form-wrapper {
    padding: 25px 20px;
    margin: 60px auto;
  }

  .form-logo {
    width: 70%;
  }
}

@import url('https://fonts.cdnfonts.com/css/avenir-next-lt-pro');

:root {
  --azul-ginez: #002B7A;
  --rojo-ginez: #E60012;
  --gris-fondo: #F4F6F9;
  --blanco: #FFFFFF;
  --fuente: 'Avenir Next LT Pro Light', 'Open Sans', sans-serif;
}

/* Fondo general */
body {
  font-family: var(--fuente);
  background: linear-gradient(135deg, #ffffff 0%, #e9edf5 50%, #dfe7f7 100%);
  color: #333;
  margin: 0;
  padding: 0;
}

/* Contenedor principal */
.form-container {
  background: var(--blanco);
  border-radius: 18px;
  box-shadow: 0 8px 22px rgba(0, 43, 122, 0.15);
  max-width: 760px;
  margin: 50px auto;
  padding: 40px;
  border-top: 8px solid var(--azul-ginez);
  position: relative;
  overflow: hidden;
  transition: transform 0.3s ease;
}
.form-container:hover {
  transform: translateY(-3px);
}

/* T√≠tulo principal */
h1 {
  font-family: var(--fuente);
  font-size: 30px;
  color: var(--azul-ginez);
  text-align: center;
  font-weight: 600;
  margin-top: 0;
  margin-bottom: 15px;
  letter-spacing: 0.5px;
}

/* Descripci√≥n */
p {
  text-align: center;
  font-size: 15px;
  color: #555;
  margin-bottom: 25px;
  line-height: 1.5;
}

/* Cada pregunta en su propio bloque */
.form-field {
  background: var(--gris-fondo);
  border: 1px solid #dde3ed;
  border-radius: 14px;
  padding: 16px 20px;
  margin-bottom: 16px;
  box-shadow: 0 2px 6px rgba(0, 43, 122, 0.08);
  transition: all 0.25s ease;
}
.form-field:hover {
  box-shadow: 0 4px 12px rgba(0, 43, 122, 0.15);
  transform: translateY(-1px);
}

/* Etiquetas */
label {
  font-weight: 600;
  color: var(--azul-ginez);
  font-size: 14px;
  display: block;
  margin-bottom: 6px;
}

/* Campos */
input, select, textarea {
  width: 100%;
  padding: 10px 12px;
  font-size: 14px;
  border-radius: 8px;
  border: 1px solid #cfd8e3;
  font-family: var(--fuente);
  background-color: #fafbfc;
  transition: all 0.2s ease-in-out;
}
input:focus, select:focus, textarea:focus {
  border-color: var(--rojo-ginez);
  outline: none;
  box-shadow: 0 0 0 3px rgba(230, 0, 18, 0.2);
}

/* === BOT√ìN SUBMIT (versi√≥n anterior restaurada) === */
button[type="submit"] {
  width: 100%;
  background: linear-gradient(90deg, var(--azul-ginez), var(--rojo-ginez));
  color: var(--blanco);
  font-weight: bold;
  font-size: 16px;
  padding: 14px;
  border: none;
  border-radius: 12px;
  cursor: pointer;
  letter-spacing: 0.6px;
  box-shadow: 0 6px 14px rgba(0, 43, 122, 0.25);
  transition: all 0.3s ease;
}
button[type="submit"]:hover {
  background: linear-gradient(90deg, var(--rojo-ginez), var(--azul-ginez));
  transform: translateY(-2px);
  box-shadow: 0 8px 20px rgba(0, 43, 122, 0.35);
}

/* Marca de agua difuminada */
.form-container::after {
  content: "";
  background: url('https://www.dropbox.com/scl/fi/plulfm317h1yav632a1ei/Logo.png?rlkey=i1ptyibt7mvw3iiq2pollnk2o&raw=1') no-repeat center;
  background-size: 320px;
  opacity: 0.05;
  position: absolute;
  top: 10%;
  left: 50%;
  transform: translateX(-50%);
  width: 80%;
  height: 80%;
  pointer-events: none;
}

/* Footer o cr√©ditos */
.form-footer {
  text-align: center;
  font-size: 12px;
  color: #777;
  margin-top: 25px;
}
.form-footer::after {
  content: "üî¨ Grupo Ginez¬Æ | Control de Calidad y Desarrollo";
  display: block;
  color: var(--azul-ginez);
  font-weight: 600;
  margin-top: 5px;
  font-size: 13px;
}

/* Responsivo */
@media (max-width: 600px) {
  .form-container {
    padding: 25px;
  }
  h1 {
    font-size: 24px;
  }
}

