// Normaliza y devuelve YYMMDD desde fecha ISO / DD/MM/YYYY / YYYY-MM-DD
function fechaLoteYYMMDD(fechaRaw) {
  if (!fechaRaw) return "000000";

  const txt = String(fechaRaw).trim();
  const solo = txt.split("T")[0];

  // DD/MM/YYYY
  if (solo.includes("/")) {
    const [dd, mm, yyyy] = solo.split("/");
    if (dd && mm && yyyy) {
      return `${String(yyyy).slice(-2)}${mm.padStart(2, "0")}${dd.padStart(2, "0")}`;
    }
  }

  // YYYY-MM-DD
  if (solo.includes("-")) {
    const [yyyy, mm, dd] = solo.split("-");
    if (dd && mm && yyyy) {
      return `${String(yyyy).slice(-2)}${mm.padStart(2, "0")}${dd.padStart(2, "0")}`;
    }
  }

  // ISO con Z / timestamp: usa UTC
  const d = new Date(txt);
  if (!Number.isNaN(d.getTime())) {
    const yy = String(d.getUTCFullYear()).slice(-2);
    const mm = String(d.getUTCMonth() + 1).padStart(2, "0");
    const dd = String(d.getUTCDate()).padStart(2, "0");
    return `${yy}${mm}${dd}`;
  }

  return "000000";
}

// 1) Entrada desde Merge1 (ya trae coincidencias + datos insertados)
const data = ($input.first() || {}).json || {};

// 2) Si por alguna raz√≥n ya trae lote, no lo sobrescribas
const lotePrevio = String(data.lote_producto ?? "").trim();
if (lotePrevio) {
  return [{ json: data }];
}

// 3) Conteo
const coincidencias = Number(data.coincidencias ?? 0);

// 4) Campos base
const sucursal = String(data.sucursal ?? "").trim().toUpperCase();
const codigo   = String(data.codigo_producto ?? "").trim().toUpperCase();
const fechaLote = fechaLoteYYMMDD(data.fecha_fabricacion);

// 5) Acr√≥nimo
const acronimo = (typeof acronimos === "object" && acronimos[sucursal]) ? acronimos[sucursal] : "NA";

// 6) Tama√±o (solo d√≠gitos)
const tam = String(
  data.tamano_lote ??
  data["tama√±o_lote"] ??
  data.tamanoLote ??
  ""
).replace(/[^\d]/g, "");

// 7) Consecutivo (empieza en 01)
const consecutivo = String(coincidencias + 1).padStart(2, "0");

// 8) Lote final
const lote = `${fechaLote}-${acronimo}-${codigo}${tam}-${consecutivo}`;

// 9) Regresa el mismo registro + lote (para Update by id)
return [{
  json: {
    ...data,
    lote_producto: lote,
  }
}];

