// ‚úÖ Normaliza la fecha a ISO YYYY-MM-DD
function normalizarFecha(fechaRaw) {
  if (!fechaRaw) return "";
  const txt = String(fechaRaw).trim();
  let solo = txt.split("T")[0];

  // DD/MM/YYYY
  if (solo.includes("/")) {
    const [dd, mm, yyyy] = solo.split("/");
    if (dd && mm && yyyy) return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
  }

  // YYYY-MM-DD
  if (solo.includes("-")) {
    const [yyyy, mm, dd] = solo.split("-");
    if (dd && mm && yyyy) return `${yyyy}-${mm.padStart(2, "0")}-${dd.padStart(2, "0")}`;
  }

  return solo;
}

// --------- PRINCIPAL ---------

// Solo filas v√°lidas
const rows = $input.all().filter(i => i && i.json);

// Si no hay filas, no hacemos nada
if (rows.length === 0) return [];

// 1) √öltima fila = mayor row_number
let ultimo = rows[0];
for (const r of rows) {
  const rn = Number(r.json.row_number ?? 0);
  const ru = Number(ultimo.json.row_number ?? 0);
  if (rn > ru) ultimo = r;
}

// 2) Hist√≥ricos = todos menos la √∫ltima fila
const historicos = rows.filter(r =>
  Number(r.json.row_number ?? 0) !== Number(ultimo.json.row_number ?? 0)
);

// 3) Datos del registro nuevo
const item = ultimo.json;

const sucursal = String(item.sucursal ?? "").trim();
const codigo   = String(item.codigo_producto ?? "").trim();

const fechaISO = normalizarFecha(item.fecha_fabricacion ?? "");
if (!fechaISO || !fechaISO.includes("-")) {
  // Si la fecha viene vac√≠a o rara, evita romper el flujo
  item.lote_producto = "";
  return [{ json: item }];
}

const [yyyy, mm, dd] = fechaISO.split("-");
const fechaLote = `${String(yyyy).slice(-2)}${mm}${dd}`; // YYMMDD

// 4) Contar coincidencias anteriores (SOLO si ya ten√≠an lote)
const coincidencias = historicos.filter(r => {
  const j = r.json || {};

  const lotePrevio = String(j.lote_producto ?? j.lote_producto ?? j.lote ?? "").trim();
  if (lotePrevio === "") return false; // ‚úÖ clave para que el primero sea 01

  const fISO = normalizarFecha(j.fecha_fabricacion ?? "");
  return (
    fISO === fechaISO &&
    String(j.sucursal ?? "").trim() === sucursal &&
    String(j.codigo_producto ?? "").trim() === codigo
  );
}).length;

const consecutivo = String(coincidencias + 1).padStart(2, "0"); // 01, 02, 03...

// 5) Acr√≥nimo y tama√±o (usa tu objeto `acronimos` ya definido arriba en tu script)
const acronimo = acronimos[sucursal] || "NA";

const tam = String(
  item.tamano_lote ??
  item["tama√±o_lote"] ??
  item.tamanoLote ??
  ""
).replace(/[^\d]/g, "");

// 6) Lote final (solo en la fila nueva)
item.lote_producto = `${fechaLote}-${acronimo}-${codigo}${tam}-${consecutivo}`;

// Important√≠simo: devolver SOLO la √∫ltima fila para que Update Row no toque las dem√°s
return [{ json: item }];
